import express from "express";
import { selectSql, updateSql } from "../database/sql";

const router = express.Router();

//    
router.get('/employee', async (req, res) => {
    const emp_res = await selectSql.getEmployee();

    // views updateEmployee  getEmployee  
    res.render('updateEmployee', {
        title: "  ",
        emp_res
    });
});

//    
router.get('/department', async (req, res) => {
    const dept_res = await selectSql.getDepartment();

    // views  updateDepartment  getDepartment  
    res.render('updateDepartment', {
        title: "  ",
        dept_res
    });
});

//     update query    
router.post('/employee', async (req, res) => {
    const vars = req.body;
    const data = {
        Salary: vars.salary
    }

    await updateSql.updateEmployee(data);

    //   /select  
    res.redirect('/select');
});

//     update query    
router.post('/department', async (req, res) => {
    const vars = req.body;
    console.log(vars.dname);

    const data = {
        Dname: vars.dname
    }
    await updateSql.updateDepartment(data);

    res.redirect('/select');
});

module.exports = router;

