// export :      

import mysql from "mysql2";

//  
const pool = mysql.createPool(
    process.env.JAWSDB_URL ?? {
        host: 'localhost',
        user: 'root',
        database: 'week10',
        password: 'Yefla14*07',
        waitForConnections: true,
        connectionLimit: 10,
        queueLimit: 0
    }
);

const promisePool = pool.promise();

// async await   

// select query. 
export const selectSql = {
    // mysql user     
    getUsers : async () => {
        const [rows] = await promisePool.query(`select * from user`);
        //console.log(rows)
        return rows
    },

    // mysql department     
    getDepartment : async () => {
        // department    
        const [rows] = await promisePool.query(`select * from department`);
        //console.log(rows)
        return rows
    },

    // mysql student  ( )    
    getStudent : async () => {
        const [rows] = await promisePool.query(`select * from student`);
        console.log(rows)
        return rows
    },
}

// delete query
export const deleteSql = {
    // department      
    deleteDepartment : async (data) => {
        //console.log('deleteSql.deleteDepartment:', data.Dnumber);
        //        
        const sql = `delete from department where Dnumber = ${data.Dnumber}`;
        await promisePool.query(sql);
    },

    deleteStudent : async (data) => {
        //  deleteStudent  
        //        
        //const sql = `delete from student where Major = "${data.Major}"`;

        // step3
        //  :  12181234   
        const sql = `delete from student where StudentNumber = 12181234`;
        await promisePool.query(sql);
    }
}

